import React, { useState, useEffect } from 'react';

const CLIENT_ID = '9f9e746f4f604bbe9331529d75394009'; // Replace with your Spotify Client ID
const REDIRECT_URI = 'http://localhost:3000/callback'; // Replace with your Redirect URI
const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize';
const RESPONSE_TYPE = 'code';

const App = () => {
    const [code, setCode] = useState(null);

    useEffect(() => {
        const urlParams = new URLSearchParams(window.location.search);
        const authCode = urlParams.get('code');

        if (authCode) {
            setCode(authCode);
            // Here you would typically send the code to your backend server to exchange it for an access token
        }
    }, []);

    const handleLogin = () => {
        window.location.href = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&show_dialog=true`;
    };

    return (
      <div className="signup-container">
          <h2>Sign Up</h2>
          <form onSubmit={handleSubmit}>
              <div className="form-group">
                  <label htmlFor="username">Username</label>
                  <input 
                      type="text" 
                      id="username" 
                      name="username" 
                      value={formData.username} 
                      onChange={handleInputChange}
                      required 
                  />
              </div>
              <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input 
                      type="email" 
                      id="email" 
                      name="email" 
                      value={formData.email} 
                      onChange={handleInputChange}
                      required 
                  />
              </div>
              <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input 
                      type="password" 
                      id="password" 
                      name="password" 
                      value={formData.password} 
                      onChange={handleInputChange}
                      required 
                  />
              </div>
              <button type="submit" className="signup-button">Sign Up</button>
          </form>
      </div>
  );
};

export default App;
