import React, { useState, useEffect } from 'react';
import './App.css';
import SpotifyWebApi from 'spotify-web-api-js';

const spotifyApi = new SpotifyWebApi();

const getTokenFromUrl = () => {
    return window.location.hash
    .substring(1)
    .split('&')
    .reduce((initial, item) => {
        let parts = item.split('=');
        initial[parts[0]] = decodeURIComponent(parts[1]);
        return initial;
    },{}); 
};

function App() {
    const [spotifyToken, setSpotifyToken] = useState("");
    const [topTracks, setTopTracks] = useState([])
    const [loggedIn, setLoggedIn] = useState(false)

    useEffect(() => {
        const tokenInfo = getTokenFromUrl();
        console.log("This is what we derived from the URL: ", tokenInfo);
        const spotifyToken = tokenInfo.access_token;
        window.location.hash = "";
        console.log("This is our spotify token:", spotifyToken);

        if (spotifyToken) {
            setSpotifyToken(spotifyToken)
            //use spotify api
            spotifyApi.setAccessToken(spotifyToken)
            spotifyApi.getMe().then((user) => {
                console.log(user)
            })  
            setLoggedIn(true)
        }
    })

    const getTopTracks = () => {
        spotifyApi.getMyTopTracks().then((response) => {
            console.log("This is our top tracks response: ", response)
            setTopTracks(response.items)
        })
    }

    useEffect(() => {
        if (loggedIn) {
            getTopTracks();
        }
    }, [loggedIn]);

    return (
        <div className="App">
            {!loggedIn && 
                <a href="http://localhost:3001/login">Login to Spotify</a>
            }
            {loggedIn && (
                <div>
                    <h2>Top Tracks</h2>
                    <ul>
                        {topTracks.map((track, index) => (
                            <li key={index}>
                                {track.name} by {track.artists.map(artist => artist.name).join(", ")}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
